graphql.query:
query {
  ethereum(network: bsc) {
    dexTrades(options: {desc: "trades", limit: 100}) {
      poolToken: smartContract {
        address {
          address
        }
      }
      exchange {
        fullName
      }
      baseCurrency {
        symbol
      }
      quoteCurrency {
        symbol
      }
      trades: count
    }
  }
}

query {
  ethereum(network: bsc) {
    dexTrades(options: {limit: 10}
      baseCurrency: {is: "0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56"}
    	quoteCurrency:{is: "0x2170Ed0880ac9A755fd29B2688956BD959F933F8"}
    ){

      
      baseCurrency {
        symbol
        address
      }
      baseAmount
      
      quoteCurrency {
        symbol
        address
      }
      quoteAmount

      quotePrice
      

    }
  }
}

